new Vue({el:"#app",data:{userSearch:"",showCart:!1},methods:{showBasket(){this.showCart=!this.showCart},getJson(t){return fetch(t).then((t=>t.json())).catch((t=>this.$refs.error.setText(t)))},postJson(t,e){return fetch(t,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(e)}).then((t=>t.json())).catch((t=>this.$refs.error.setText(t)))},putJson(t,e){return fetch(t,{method:"PUT",headers:{"Content-Type":"application/json"},body:JSON.stringify(e)}).then((t=>t.json())).catch((t=>this.$refs.error.setText(t)))},delJson(t,e){return fetch(t,{method:"DELETE",headers:{"Content-Type":"application/json"},body:JSON.stringify(e)}).then((t=>t.json())).catch((t=>this.$refs.error.setText(t)))}}});